// Slideshow.js
import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';


const images = [
  'url(public/images/bg/15.jpg)',
  'url(/Users/kushalshah7/Desktop/tourism/public/images/bg/16.jpg)',
  'url(../images/bg/17.jpg)',
  // Add more image URLs here
];

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const SlideshowWrapper = styled.div`
  position: relative;
  height: 800px;
  overflow: hidden;
`;

const BackgroundImage = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center;
  animation: ${fadeIn} 1s ease-in-out;
`;

const Slideshow = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // Change image every 3 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <SlideshowWrapper className="intro15">
      <BackgroundImage style={{ backgroundImage: images[currentImageIndex] }} />
    </SlideshowWrapper>
  );
};

export default Slideshow;
